import React, { useContext } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { AuthContext } from '../context/AuthContext'; // Załóżmy, że masz kontekst AuthContext

const Navigation = () => {
    const { isAuthenticated, userRole, logout } = useContext(AuthContext);
    const navigate = useNavigate();

    const handleLogout = () => {
        logout();
        navigate('/');
    };

    if (!isAuthenticated) {
        return null;
    }

    return (
        <nav>
            <ul>
                <li><Link to="/">Strona główna</Link></li>
                <li><Link to="/dashboard">Dashboard</Link></li>
                {userRole === 'admin' && (
                    <>
                        <li><Link to="/admin">Panel Admina</Link></li>
                        <li><Link to="/employees/list">Lista pracowników</Link></li>
                        <li><Link to="/services/add">Dodaj usługę</Link></li>
                        <li><Link to="/services/list">Lista usług</Link></li>
                    </>
                )}
                <li><Link to="/profile">Profil</Link></li>
                <li><button onClick={handleLogout}>Wyloguj się</button></li>
            </ul>
        </nav>
    );
};

export default Navigation;
